import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import SpotifyWebApi from 'spotify-web-api-js';
import './App.css'; // Import your CSS styles

const spotifyApi = new SpotifyWebApi();

const App = () => {
  const [messages, setMessages] = useState([]);
  const [todos, setTodos] = useState([]);
  const [showTodos, setShowTodos] = useState(false);
  const [spotifyToken, setSpotifyToken] = useState(null);
  const chatContainerRef = useRef(null);
  const robotEyesRef = useRef([null, null]); // Ref for robot's eyes

  useEffect(() => {
    fetchTodos();
    initSpotify();
  }, []);

  const initSpotify = async () => {
    // Fetch Spotify access token (replace with your method to get token)
    const token = await fetchSpotifyToken();
    setSpotifyToken(token);

    // Set Spotify access token for the Spotify Web API library
    spotifyApi.setAccessToken(token);
  };

  const fetchSpotifyToken = async () => {
    // Replace with your method to fetch Spotify access token
    // Example: return await axios.get('your-auth-endpoint').then(res => res.data.token);
    return 'YOUR_SPOTIFY_ACCESS_TOKEN';
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    const newMessage = e.target.message.value;
    if (newMessage.trim() !== '') {
      const userMessage = { text: newMessage, sender: 'user' };
      setMessages([...messages, userMessage]);

      try {
        const response = await axios.post('http://localhost:8080/send-message', {
          message: newMessage
        });

        const botMessage = { text: response.data.message, sender: 'assistant' };
        setMessages(prevMessages => [...prevMessages, botMessage]);

        if (response.data.message.startsWith('Opening')) {
          const siteUrl = response.data.message.substring(8).trim();
          window.open(siteUrl, '_blank');
        }

        if (response.data.message.startsWith('Play a song by')) {
          const artist = response.data.message.substring(15).trim();
          playSongByArtist(artist);
        }

        // Handle other message responses as needed

      } catch (error) {
        console.error('Error sending message:', error);
      }

      e.target.message.value = '';
    }
  };

  const playSongByArtist = async (artist) => {
    try {
      // Search for tracks by artist
      const searchResult = await spotifyApi.searchTracks(`artist:${artist}`, { limit: 1 });

      // Extract the first track's URI
      const trackUri = searchResult.tracks.items[0].uri;

      // Play the track using the Spotify Web Playback SDK
      const player = new window.Spotify.Player({
        name: 'Web Playback SDK Quick Start Player',
        getOAuthToken: cb => { cb(spotifyToken); }
      });

      // Error handling
      player.addListener('initialization_error', ({ message }) => { console.error(message); });
      player.addListener('authentication_error', ({ message }) => { console.error(message); });
      player.addListener('account_error', ({ message }) => { console.error(message); });
      player.addListener('playback_error', ({ message }) => { console.error(message); });

      // Playback status updates
      player.addListener('player_state_changed', state => { console.log(state); });

      // Ready
      player.addListener('ready', ({ device_id }) => {
        console.log('Ready with Device ID', device_id);
        // Play track
        spotifyApi.play({ uris: [trackUri], device_id }).then(() => {
          console.log('Playing track');
        });
      });

      // Connect to the player
      player.connect();
    } catch (error) {
      console.error('Error playing song:', error);
    }
  };

  const fetchTodos = async () => {
    try {
      const response = await axios.get('http://localhost:8080/todos');
      setTodos(response.data);
    } catch (error) {
      console.error('Error fetching todos:', error);
    }
  };

  useEffect(() => {
    // Scroll chat to bottom whenever messages change
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);

  // Function to update robot's eyes position
  const updateRobotEyes = (e) => {
    const mouseX = e.pageX;
    const mouseY = e.pageY;
    const eye1 = robotEyesRef.current[0];
    const eye2 = robotEyesRef.current[1];

    if (eye1 && eye2) {
      const eyeSize = 12; // Adjust based on eye size
      const eyeCenterX = eye1.getBoundingClientRect().left + eyeSize / 2;
      const eyeCenterY = eye1.getBoundingClientRect().top + eyeSize / 2;

      const deltaX1 = mouseX - eyeCenterX;
      const deltaY1 = mouseY - eyeCenterY;
      const distance1 = Math.sqrt(deltaX1 * deltaX1 + deltaY1 * deltaY1);

      const deltaX2 = mouseX - (eye2.getBoundingClientRect().left + eyeSize / 2);
      const deltaY2 = mouseY - (eye2.getBoundingClientRect().top + eyeSize / 2);
      const distance2 = Math.sqrt(deltaX2 * deltaX2 + deltaY2 * deltaY2);

      const maxEyeMove = 3; // Adjust eye movement sensitivity

      eye1.style.transform = `translate(${deltaX1 / distance1 * maxEyeMove}px, ${deltaY1 / distance1 * maxEyeMove}px)`;
      eye2.style.transform = `translate(${deltaX2 / distance2 * maxEyeMove}px, ${deltaY2 / distance2 * maxEyeMove}px)`;
    }
  };

  useEffect(() => {
    // Add mousemove event listener to update robot's eyes position
    document.addEventListener('mousemove', updateRobotEyes);

    return () => {
      // Clean up event listener on component unmount
      document.removeEventListener('mousemove', updateRobotEyes);
    };
  }, []);

  return (
    <div className="App">
      <div className="robot-face" style={{ top: '10px', right: '10px' }}>
        <div className="eye" ref={el => robotEyesRef.current[0] = el}></div>
        <div className="eye" style={{ left: '50%' }} ref={el => robotEyesRef.current[1] = el}></div>
        <div className="mouth"></div>
      </div>
      <h1>Amute</h1>
      <div className="chat-container" ref={chatContainerRef}>
        <div className="chat-messages">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.sender}`}>
              {message.text}
            </div>
          ))}
        </div>
        <form onSubmit={handleSendMessage}>
          <input type="text" name="message" className="message-input" placeholder="Type your message..." />
          <button type="submit" className="send-button">Send</button>
        </form>
        {showTodos && (
          <div className="todo-list">
            <h2>Your To-Do List</h2>
            <ul>
              {todos.map(todo => (
                <li key={todo.id} className={todo.completed ? 'completed' : ''}>
                  <span>{todo.task}</span>
                  <button onClick={() => removeTodoTask(todo.id)}>Remove</button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
