import React, { useState } from 'react';
import './App.css'; // Include your CSS file for styling

const App = () => {
  const [messages, setMessages] = useState([]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    const newMessage = e.target.message.value;
    if (newMessage.trim() !== '') {
      setMessages([...messages, { text: newMessage, sender: 'user' }]);
      // Here you can send the message to your backend or handle it as needed
      // Example: sendToAssistant(newMessage);
      e.target.message.value = '';
    }
  };

  return (
    <div className="App">
      <h1>Chrome Extension Assistant</h1>
      <div className="chat-container">
        <div className="chat-messages">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.sender}`}>
              {message.text}
            </div>
          ))}
        </div>
        <form onSubmit={handleSendMessage}>
          <input type="text" name="message" placeholder="Type your message..." />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
};

export default App;
