import React, { useState } from 'react';
import axios from 'axios';
import './App.css'; // Include your CSS file for styling

const App = () => {
  const [messages, setMessages] = useState([]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    const newMessage = e.target.message.value;
    if (newMessage.trim() !== '') {
      // Add user message to state for immediate UI update
      const userMessage = { text: newMessage, sender: 'user' };
      setMessages([...messages, userMessage]);

      try {
        let responseMessage = '';

        // Check if user command is to open a website
        if (newMessage.toLowerCase().startsWith('open ') && newMessage.split(' ').length > 1) {
          const url = newMessage.split(' ')[1];
          responseMessage = `Opening ${url}...`;

          // Open website in new tab
          window.open(`http://${url}`, '_blank');
        } else {
          // Send message to backend if not a direct command
          const response = await axios.post('http://localhost:8080/send-message', {
            message: newMessage
          });
          responseMessage = response.data.message;
        }

        // Add bot response to state after handling message
        const botMessage = { text: responseMessage, sender: 'assistant' };
        setMessages([...messages, botMessage]);

        // Optionally handle response from backend
        console.log('Message sent successfully:', responseMessage);

      } catch (error) {
        console.error('Error sending message:', error);
        // Handle error (e.g., show error message to user)
      }

      // Clear input field after sending message
      e.target.message.value = '';
    }
  };

  return (
    <div className="App">
      <h1>Chrome Extension Assistant</h1>
      <div className="chat-container">
        <div className="chat-messages">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.sender}`}>
              {message.text}
            </div>
          ))}
        </div>
        <form onSubmit={handleSendMessage}>
          <input type="text" name="message" placeholder="Type your message..." />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
};

export default App;
