import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css'; // Assuming you have your CSS styles in App.css

const App = () => {
  const [messages, setMessages] = useState([]);
  const chatContainerRef = useRef(null);
  const reminderSound = useRef(new Audio('/path/to/sound.mp3'));

  const handleSendMessage = async (e) => {
    e.preventDefault();
    const newMessage = e.target.message.value;
    if (newMessage.trim() !== '') {
      // Add user message to state for immediate UI update
      const userMessage = { text: newMessage, sender: 'user' };
      setMessages([...messages, userMessage]);

      try {
        // Send message to backend
        const response = await axios.post('http://localhost:8080/send-message', {
          message: newMessage
        });

        // Add bot response to state after receiving response from backend
        const botMessage = { text: response.data.message, sender: 'assistant' };
        setMessages(prevMessages => [...prevMessages, botMessage]);

        // Check if the response is for opening a site
        if (response.data.message.startsWith('Opening')) {
          // Open the site in a new tab
          const siteUrl = response.data.message.substring(8).trim(); // Remove 'Opening ' from the start
          window.open(siteUrl, '_blank');
        }

        // Handle reminders
        if (response.data.message.startsWith('Reminder set:')) {
          const reminderMessage = response.data.message.substring(14).trim(); // Remove 'Reminder set:' from the start
          setReminder(reminderMessage);
        }

      } catch (error) {
        console.error('Error sending message:', error);
        // Handle error (e.g., show error message to user)
      }

      // Clear input field after sending message
      e.target.message.value = '';
    }
  };

  // Function to handle setting reminders
  const setReminder = (reminderMessage) => {
    const reminderParts = reminderMessage.split(' at ');
    if (reminderParts.length === 2) {
      const [task, time] = reminderParts;
      const reminderTime = new Date(time);
      const now = new Date();

      if (reminderTime > now) {
        const timeout = reminderTime - now;
        setTimeout(() => {
          alert(`Reminder: ${task}`);
          reminderSound.current.play();
        }, timeout);
      }
    } else if (reminderMessage.includes('every')) {
      const [task, interval] = reminderMessage.split(' every ');
      const intervalMinutes = parseInt(interval, 10);

      if (!isNaN(intervalMinutes)) {
        setInterval(() => {
          alert(`Reminder: ${task}`);
          reminderSound.current.play();
        }, intervalMinutes * 60 * 1000);
      }
    } else {
      console.error('Invalid reminder format');
    }
  };

  useEffect(() => {
    // Scroll chat to bottom whenever messages change
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="App">
      <h1>Chrome Extension Chatbot</h1>
      <div className="chat-container" ref={chatContainerRef}>
        <div className="chat-messages">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.sender}`}>
              {message.text}
            </div>
          ))}
        </div>
        <form onSubmit={handleSendMessage}>
          <input type="text" name="message" className="message-input" placeholder="Type your message..." />
          <button type="submit" className="send-button">Send</button>
        </form>
      </div>
    </div>
  );
};

export default App;
